import React, {Fragment} from 'react';
import { PropTypes } from 'prop-types';
import { connect } from 'react-redux';
import {Link } from 'react-router-dom'
import { addComment } from './../../actions/post';
import moment from 'moment';
import {deleteComment} from '../../actions/post' 
import { deleteEducation } from './../../actions/profile';

const CommentForm = ({ postId, deleteComment, auth,
  comment: {_id, text, name, avatar, user, date } }) => {
  return (  
    <div class="post bg-white p-1 my-1">
      <div>
        <Link to={`/profile/${user}`}>
          <img
            class="round-img"
            src={avatar}
            alt=""
          />
          <h4> {name} </h4>
        </Link>
      </div>
      <div>
        <p class="my-1">
          {text}
        </p>
        <p class="post-date">
            Posted on { moment(date).format('MM/DD/YYYY') } 
        </p>
        {!auth.loading && user === auth.user._id && (
          <button onClick={e => deleteComment(postId, _id)} type="button" className="btn btn-danger">
            <i className="fasfa-times"></i>
          </button>
        )}
      </div>
    </div>
  );
}

CommentForm.propTypes = {
  addComment: PropTypes.func.isRequired,
  comment: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  deleteComment: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
  auth: state.auth
})
 
export default connect(mapStateToProps, {addComment, deleteComment})(CommentForm);
