import React, { Fragment, useState, useEffect} from 'react';
import { connect } from 'react-redux';
import { addExperience } from './../../actions/profile';
import { PropTypes } from 'prop-types';
import { withRouter } from 'react-router-dom';

const AddExperience = () => {
  const [formData, setFormData] = useState({
    company: '',
    title: '',
    loaction: '',
    from: '', 
    to: '', 
    current: false, 
    description: ''
  })

  const [toDateDisabled, toggleDisbaled] = useState(false);

  const { company, title, location, from, to, current, description} = formData;

  const onChange = e => setFormData({...formData, [e.target.name]: e.target.value})

  return (  
    <Fragment>
      <h1 class="large text-primary">
        Add An Experience
      </h1>
      <p class="lead">
        <i class="fas fa-code-branch"></i> Add any developer/programming
        positions that you have had in the past
      </p>
      <small>* = required field</small>
      <form class="form">
        <div class="form-group">
          <input type="text" placeholder="* Job Title" name="title" value={title} 
            onChange={(e => onChange(e))} required />
        </div>
        <div class="form-group">
          <input type="text" placeholder="* Company" name="company" value={company} 
            onChange={(e => onChange(e))}required />
        </div>
        <div class="form-group">
          <input type="text" placeholder="Location" name="location" value={location} 
            onChange={(e => onChange(e))}/>
        </div>
        <div class="form-group">
          <h4>From Date</h4>
          <input type="date" name="from" value={from} 
            onChange={(e => onChange(e))}/>
        </div>
         <div class="form-group">
          <p><input type="checkbox" name="current" checked={current} value={current} onChange={(e =>  {    
            setFormData({ ...formData, current: !current });
            toggleDisbaled(!toDateDisabled)
          })} /> {' '} Current Job</p>
        </div>
        <div class="form-group">
          <h4>To Date</h4>
          <input type="date" name="to" value={to} onChange={(e => onChange(e))} 
              disabled={toDateDisabled ? 'disabled' : ''}
          />
        </div>
        <div class="form-group">
          <textarea
            name="description"
            cols="30"
            rows="5"
            placeholder="Job Description"
            value={description} 
            onChange={(e => onChange(e))}
          ></textarea>
        </div>
        <input type="submit" class="btn btn-primary my-1" />
        <a class="btn btn-light my-1" href="dashboard.html">Go Back</a>
      </form>
    </Fragment>
  );
}

AddExperience.propTypes = {
  addExperience: PropTypes.func.isRequired
}
 
export default connect(null, { addExperience })(AddExperience);
