import { GET_POSTS, POST_ERROR, UPDATE_LIKES, 
        DELETE_POST, ADD_POST, GET_POST, ADD_COMMENT, REMOVE_COMMENT } from '../actions/types';


const initialState = {
  posts: [],
  post: null,
  loading: true,
  error: {}
}

export default function(state=initialState, action) {
  switch(action.type) {
    case GET_POSTS:
      return {
        ...state,
        posts: action.payload,
        loading: false
      }
    case GET_POST: 
      return {
        ...state,
        post: action.payload,
        loading: false
      }
    case ADD_POST: 
      return {
        ...state,
        posts: [action.payload, ...state.posts],
        loading: false
      }
    case DELETE_POST:
      return {
        ...state,
        posts: state.posts.filter(post => post._id !== action.payload), //this post is deleted
        loading: false
      }
    case POST_ERROR:
      return {
        ...state,
        error: action.payload,
        loading: false
      }
    case UPDATE_LIKES:
      return {
        ...state,
        posts: state.posts.map(post => post._id === action.payload.id ? { ...post, likes:
        action.payload.likes} : post ),
        loading: false
      }
    case ADD_COMMENT: 
      return {
        ...state,
        post: {...state.post, comments: action.payload },
        loading: false
      }
    case REMOVE_COMMENT: 
      return {
        ...state,
        post: {
          ...state,
          comments: state.post.comments.filter(comment => comment._id !== action.payload)
        },
        loading: false
      }
    default:
      return state;
  }
}
