import { GET_POSTS, POST_ERROR, UPDATE_LIKES, DELETE_POST } from '../actions/types';

const initialState = {
  posts: [],
  post: null,
  loading: true,
  error: {}
}

export default function(state=initialState, action) {
  switch(action.type) {
    case GET_POSTS:
      return {
        ...state,
        posts: action.payload,
        loading: false
      }
    case ADD_POST: 
      return {
        ...state,
        posts: [...state.posts, action.payload],
        loading: false
      }
    case DELETE_POST:
      return {
        ...state,
        posts: state.post.filter(post => post._id !== action.payload), //this post is deleted
        loading: false
      }
    case POST_ERROR:
      return {
        ...state,
        error: action.payload,
        loading: false
      }
    case UPDATE_LIKES:
      return {
        ...state,
        posts: state.posts.map(post => post._id === action.payload.id ? { ...post, likes:
        action.payload.likes} : post ),
        loading: false
      }
    default:
      return state;
  }
}
